mapper.select:
select a.*, b.cap_file_name as photo from cinema_actor_info a left outer join
    (
        select * from cinema_actor_photo 
        group by cap_cai_seq
    ) b on 
a.cai_seq = b.cap_cai_seq
where cai_name like '%${keyword}%'

and cai_country = #{country}

limit 10 offset #{offset};

select ceil(count(*)/10) from cinema_actor_info
where cai_name like '%${keyword}%'

and cai_country = #{country}

select mi_seq, mi_title, mi_country, mi_year, ( select count(*) from movie_actor_casting_info where maci_mi_seq = mi_seq ) as actor_count from movie_info where mi_title like '%${keyword}%'
and mi_country = #{country}
order by mi_opening_dt desc limit 10 offset #{offset}

select ceil(count(*)/10) from movie_info
where mi_title like '%${keyword}%'

    and mi_country = #{country}

mapper.insert:
INSERT INTO movie_actor_casting_info
(maci_cai_seq, maci_mi_seq, maci_casting_name, maci_role)
VALUES(#{maci_cai_seq}, #{maci_mi_seq}, #{maci_casting_name}, #{maci_role});

mapper.update:
UPDATE movie_actor_casting_info
SET
    maci_casting_name=#{maci_casting_name}, maci_role=#{maci_role}
WHERE
    maci_seq=#{maci_seq}
